import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useHistory } from "react-router-dom";
import { Table, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Styles.css';
import moment from 'moment';
// import { useState } from 'react';

function ListUser(props) {

    var CartsOld = localStorage.getItem('Carts');
    if (CartsOld == null) {
        CartsOld = [];
    }
    else {
        CartsOld = JSON.parse(CartsOld)
    }
    const [Carts, setCarts] = useState(CartsOld);

    var CartsOld = localStorage.getItem('Carts');
    if (CartsOld == null) {
        localStorage.setItem('Carts', JSON.stringify([]));
    }

    const [User, setUser] = useState([]);
    const [searchFromText, setSearchFromText] = useState(User);
    const [searchToText, setSearchToText] = useState(User);
console.log(searchFromText,"searchFromTesearchToTextxt",searchToText);
    useEffect(() => {
        getUserList()
    }, [])

    const getUserList = () => {
        axios.get('http://localhost:8001/Admin/getTicket')
            .then((response) => {
                setUser(response.data)
            })
        // .catch((error) => {
        // console.log(error);
        // })
    };

    const handleSearch = (event) => {

       setSearchFromText(event.target.value)
      setSearchToText(event.target.value)
        }

    const handleSubmit = () =>{

    }  

    const quantityChanges = (item, addOrLess) => {
        var dataCopy = JSON.parse(JSON.stringify(User));
        const check = dataCopy.findIndex(element => element._id === item._id);
        if (check !== -1) {
            if (typeof dataCopy[check].qty == 'undefined') {
                dataCopy[check].qty = 0;
            }
            if (addOrLess == 'add') {
                dataCopy[check].qty++;
            }
            else {
                dataCopy[check].qty--;
            }
            if (dataCopy[check].qty < 1) {
                dataCopy[check].qty = 0;
            }
            setUser(dataCopy);
        }
    }

    const handleClickId = (item) => {
        var hastoken = localStorage.getItem('savedToken');
        if (!hastoken) {
            alert("go first login")
            props.history.push("/login")
        }
        else {
            var CartsCopy = JSON.parse(JSON.stringify(Carts));
            console.log(CartsCopy, "CartsCopyCartsCopy");
            const check = CartsCopy.findIndex(element => element._id === item._id);
            if (check == -1) {
                if (typeof item.Quantity == 'undefined') {
                    item.Quantity = 1;
                }
                CartsCopy.push(item);
                setCarts(CartsCopy);
                localStorage.setItem('Carts', JSON.stringify(CartsCopy));
            }
            else {
                alert("Already selected this cart")
            }
        }

        // history.push('/cart/' + id)
        // axios.get('http://localhost:8001/User/buyTicket' + id)
        //     .then((response) => {
        //         console.log(response, 'Buy responce')
        //     })
    }

    // // exclude column list from filter
    // const handleSearch = value => {
    //     // setSearchText(value);
    //     // filterData(value);
    // };



    // // filter records by search text
    // const filterData = (value) => {
    //     let lowercasedValue = value.trim();
    //     let Search;

    //     {
    //         User && User.length > 0 && User.map(item => {
    //             Search = item.Price

    //         })
    //     }
    //     let filteredUser = User.filter(el => el.Price == Search)
    //     // console.log(filteredUser,"filteredUserlowercasedValue",lowercasedValue);

    // }


    const emptyToken = () => {
        console.log("hii deleted");
        localStorage.removeItem('savedToken');
        // props.history.push('/login')
        props.history.push('/login')

        // localStorage.removeItem('Carts'._id)
        // getItemList()
    }


    return (
        <div>
            <h3>List of Lottery_Ticket Details</h3>

            <section>

                    <div >
                    <div className="SearchBar">
                        <label>From Price:</label>
                        <input
                            placeholder="Type to search..."
                            name="FromSearch"
                            value={searchFromText}
                            type="text"
                            onChange={(e) => handleSearch(e.target.value)}
                        />
                        <label>To Price:</label>
                        <input
                            placeholder="Type to search..."
                            name="ToSearch"
                            value={searchToText}
                            type="text"
                            onChange={(e) => handleSearch(e.target.value)}
                        />
                        </div>  
                        <div className="col-md-12 left">
                        <Button
                            class="btn btn-success"
                            onSubmit={handleSubmit}>Submit</Button></div>

                </div>
                {/* <div className="SearchBar">
                    <div style={{ margin: '10 auto', marginTop: '3%' }}>

                    </div>
                </div>
                <div className="col-md-12 left">

                </div> */}

            </section>

            <Table hover bordered striped responsive size="sm">

                <thead>
                    <tr>
                        <th>#</th>
                        <th>Ticket_Id</th>
                        <th>Ticket_Name</th>
                        <th>Price</th>
                        <th>qty</th>
                        <th>Start_Date</th>
                        <th>End_Date</th>
                        <th>Status</th>
                        <th>Action_qty</th>

                        <th>Buy_Ticket</th>
                        {/* <th></th> */}
                    </tr>
                </thead>

                <tbody>
                    {
                        User && User.length > 0 && User.map((item, i) => {

                            return (
                                <tr key={i}>
                                    <td>{i + 1}</td>
                                    <td>{item.Ticket_Id}</td>
                                    <td>{item.Ticket_Name}</td>
                                    <td>{item.Price}</td>
                                    <td>{item.Quantity}</td>
                                    {/* <td>{user.Start_Date  ",h:mm:ss"}</td> */}
                                    <td>{moment.utc(item.Start_Date).format('MMM DD YYYY ')}</td>
                                    {/* <td>{user.End_Date}</td> */}
                                    <td>{moment.utc(item.End_Date).format('MMM DD YYYY')}</td>
                                    <td>{item.Status}</td>
                                    <td>
                                        <div key={item.id}>
                                            Add: <input type="submit" value="+" onClick={() => quantityChanges(item, 'add')} />
                                            Less: <input type="submit" value="-" onClick={() => quantityChanges(item, 'less')} />
                                        </div>

                                    </td>


                                    <td>
                                        <div key={item.id}>
                                            <Button variant="info" onClick={() => handleClickId(item)} className="btn btn-primary">Buy</Button>
                                            ({item.qty ? item.qty : 0})
                                        </div>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
                <div className="col-md-12 text-center">
                    <Link to='/login'>
                        <Button
                            class="btn btn-success"
                            onClick={emptyToken}
                        >LogOut</Button>
                    </Link>
                </div>

            </Table>
        </div>
    )

}
export default ListUser;
