import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useCart } from "react-use-cart";
import { useHistory } from "react-router-dom";
import { Table, Button } from 'react-bootstrap';
import moment from 'moment';

const CheckOut = (props) => {
  const [checkOut, setcheckOut] = useState([]);



  useEffect(() => {
    getItemList();

  }, [])



  const getItemList = () => {
    var mycheckOut = JSON.parse(localStorage.getItem('Carts'))
    setcheckOut(mycheckOut)
  };


const handleClickId = (item) => {
    var hastoken = localStorage.getItem('savedToken');
    if (!hastoken) {
        alert("go first login")
        props.history.push("/login")
    }else {
        var CartsCopy = JSON.parse(JSON.stringify(Carts));
        const check = CartsCopy.findIndex(element => element._id === item._id);
        if (check == -1) {
            if (typeof item.Quantity == 'undefined') {
                item.Quantity = 1;
            }
            CartsCopy.push(item);
            setCarts(CartsCopy);
            localStorage.setItem('Carts', JSON.stringify(CartsCopy));
        }else {
            alert("Already selected this cart")
        }
    }

    // history.push('/cart/' + id)
    // axios.get('http://localhost:8001/User/buyTicket' + id)
    //     .then((response) => {
    //         console.log(response, 'Buy responce')
    //     })
}

  return (
    <div>
       <section className="py-4 container">
        <div className="row justify-content-center">
          <div className="col-12">
            {/* <h5>Cart({totalUniqueItems}) Total Items:({totalItems})</h5> */}
            <table className="table table-light table-hover m-0">
              <thead>
                <tr>
                        <th>#</th>
                        <th>Ticket_Id</th>
                        <th>Ticket_Name</th>
                        <th>Price</th>
                        <th>qty</th>
                        <th>Start_Date</th>
                        <th>End_Date</th>
                        <th>Status</th>
                        <th>Action_qty</th>
                </tr>
              </thead>
              <tbody>
              {
                       checkOut && checkOut.length > 0 && checkOut.map((item, i)=> {
                            return (
                                <tr key={i}>
                                    <td>{i + 1}</td>
                                    <td>{item.Ticket_Id}</td>
                                    <td>{item.Ticket_Name}</td>
                                    <td>{item.Price}</td>
                                    <td>{item.Quantity}</td>
                                    {/* <td>{user.Start_Date  ",h:mm:ss"}</td> */}
                                    <td>{moment.utc(item.Start_Date).format('MMM DD YYYY ')}</td>
                                    {/* <td>{user.End_Date}</td> */}
                                    <td>{moment.utc(item.End_Date).format('MMM DD YYYY')}</td>
                                    <td>{item.Status}</td>

                                    <td>
                                        <div key={item.id}>
                                            <Button variant="info" onClick={() => handleClickId(item)} className="btn btn-primary">Buy</Button>
                                            ({item.qty ? item.qty : 0})
                                        </div>
                                    </td>
                                </tr>
                            )
                        })
                    }
              </tbody>

            </table>
          </div>
        </div>
      </section>    
    </div>
  )

}

export default CheckOut;
