import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useHistory } from "react-router-dom";
import { Table, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Styles.css';
import moment from 'moment';
// import { useState } from 'react';

function ListUser(props) {

    var CartsOld = localStorage.getItem('Carts');
    if (CartsOld == null) {
        CartsOld = [];
    }
    else {
        CartsOld = JSON.parse(CartsOld)
    }
    const [Carts, setCarts] = useState(CartsOld);

    var CartsOld = localStorage.getItem('Carts');
    if (CartsOld == null) {
        localStorage.setItem('Carts', JSON.stringify([]));
    }

    const [User, setUser] = useState([]);
    const [searchFromText, setSearchFromText] = useState('');
    const [searchToText, setSearchToText] = useState('');
    const [Price, setPrice] = useState('');
    const [Quantity, setQuantity] = useState('');
    const [state, setState] = useState({
        Price: Price,
        Quantity: Quantity,
    })
    useEffect(() => {
        getUserList()
    }, [])

    useEffect(() => {
        getData()
    }, [Carts])


    const getData = ()=>{

        Carts && Carts.length>0 && Carts.map(el =>{
            var Price = el.Price;
            var Quantity = el.Quantity;
            setPrice(Price);
            setQuantity(Quantity);

        })

    }

    const getUserList = () => {
        axios.get('http://localhost:8001/Admin/getTicket')
            .then((response) => {
                setUser(response.data)
            })
            .catch((error) => {
                console.log(error);
            })
    };

    const handleSearch = (event) => {
        switch (event.target.name) {
            case 'FromSearch':
                setSearchFromText(event.target.value);
                break;
            case 'ToSearch':
                setSearchToText(event.target.value);
                break;
            default:
                break;
        }

    }

    const handleSubmit = (event) => {
        event.preventDefault();
        let passData = {
            minPrice: searchFromText,
            maxPrice: searchToText
        }
        axios({
            method: "post",
            url: 'http://localhost:8001/User/postTicket',
            data: passData,

            // headers: { "Content-Type": "multipart/form-data" },
        }).then((response) => {
            setUser(response.data)
        })
            .catch((error) => {
                console.log(error)
            })

    }

    const quantityChanges = (item, addOrLess) => {
        var dataCopy = JSON.parse(JSON.stringify(User));
        const check = dataCopy.findIndex(element => element._id === item._id);
        if (check !== -1) {
            if (typeof dataCopy[check].qty == 'undefined') {
                dataCopy[check].qty = 0;
            }
            if (addOrLess == 'add') {
                // alert( dataCopy[check].qty);
                var DataQty = dataCopy[check].Quantity;
                if(DataQty > dataCopy[check].qty){
                    dataCopy[check].qty++;
                }

            } else {
                dataCopy[check].qty--;
            }
            if (dataCopy[check].qty < 1) {
                dataCopy[check].qty = 0;
            }
            setUser(dataCopy);
        }
    }

    const handleClickId = (item, id) => {
        var hastoken = localStorage.getItem('savedToken');
        if (!hastoken) {
            alert("go first login")
            props.history.push("/login")
        } else {
            var CartsCopy = JSON.parse(JSON.stringify(Carts));
            // let User = JSON.parse(CartsCopy)
            // alert(User.Price)
            // return false
            // var CartsData = localStorage.getItem('Carts');
            const check = CartsCopy.findIndex(element => element.Id === item._id);

            if (check == -1) {
                if (typeof item.Quantity == 'undefined') {
                    item.Quantity = 1;

                }             
                CartsCopy.push({Quantity:item.Quantity, Id:item._id, Ticket_Id:item.Ticket_Id});
                setCarts(CartsCopy);
                localStorage.setItem('Carts', JSON.stringify(CartsCopy));
            } else {
                alert("Already selected this cart")
            }
        }

        // props.history.push('/cart')
        // axios.get('http://localhost:8001/User/buyTicket' + id)
        //     .then((response) => {
        //         console.log(response, 'Buy responce')
        //     })
    }

    const handleCheckOut = async () => {
        // console.log("come handle click fun");

        // props.history.push({
        //     pathname: "/cart",
        //     data: {}
        // });

        // props.history.push('/cart')
        const response = await axios({
            method: "get",
            url: 'http://localhost:8001/User/buyTicket',
            data: {},

            // headers: { "Content-Type": "multipart/form-data" },
        })
        console.log(response.data,"responseresponse");
            // .then((response) => {
            //     console.log(response, 'Buy responce')
            // })

    }

    const emptyToken = () => {
        console.log("hii deleted");
        localStorage.removeItem('savedToken');
        props.history.push('/login')

    }


    return (
        <div className='container'>
            <div className='col-sm-12 center'>
                <h3>List of Lottery_Ticket Details</h3>
            </div>
            <section>
                <div >
                    <form onSubmit={handleSubmit}>
                        <div className="SearchBar">
                            <label>Enter Minimum Price:</label>
                            <input
                                className="form-control"
                                type="text"
                                placeholder="Type from price..."
                                name="FromSearch"
                                value={searchFromText}
                                onChange={handleSearch}
                            />
                        </div>
                        <div className="SearchBar" >
                            <label>Enter Maximum Price:</label>
                            <input
                                className="form-control"
                                type="text"
                                placeholder="Type to price..."
                                name="ToSearch"
                                value={searchToText}
                                onChange={handleSearch}
                            />
                        </div>
                        <div className='form-group' >
                            <Button block size="lg"
                                className="btn btn-success btn-lg float-left"
                                type='submit' >Submit</Button> </div>
                    </form>
                </div>

            </section>

            <div style={{ margin: '10 auto', marginTop: '10%' }}>
                <Table hover bordered striped responsive size="sm">

                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Ticket_Id</th>
                            <th>Ticket_Name</th>
                            <th>Price</th>
                            <th>qty</th>
                            <th>Start_Date</th>
                            <th>End_Date</th>
                            <th>Status</th>
                            <th>Action_qty</th>

                            <th>Buy_Ticket</th>
                            {/* <th></th> */}
                        </tr>
                    </thead>
                    <tbody>
                        {
                            User && User.length > 0 && User.map((item, i) => {

                                return (
                                    <tr key={i}>
                                        <td>{i + 1}</td>
                                        <td>{item.Ticket_Id}</td>
                                        <td>{item.Ticket_Name}</td>
                                        <td>{item.Price}</td>
                                        <td>{item.Quantity}</td>
                                        {/* <td>{user.Start_Date  ",h:mm:ss"}</td> */}
                                        <td>{moment.utc(item.Start_Date).format('MMM DD YYYY ')}</td>
                                        {/* <td>{user.End_Date}</td> */}
                                        <td>{moment.utc(item.End_Date).format('MMM DD YYYY')}</td>
                                        <td>{item.Status}</td>
                                        <td>
                                            <div key={item.id}>
                                                Add: <input type="submit" value="+" onClick={() => quantityChanges(item, 'add')} />
                                                Less: <input type="submit" value="-" onClick={() => quantityChanges(item, 'less')} />
                                            </div>

                                        </td>
                                        <td>
                                            <div key={item.id}>
                                                <Button variant="info" onClick={() => handleClickId(item)} className="btn btn-primary">Buy</Button>
                                                ({item.qty ? item.qty : 0})
                                            </div>
                                        </td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                    <div className="col-md-12 text-center">
                        <Link to='/login'>
                            <Button
                                class="btn btn-success"
                                onClick={emptyToken}
                            >LogOut</Button>
                        </Link>
                    </div>
                </Table>
            </div>
            <div className="col-md-12 text-center">
                <Button
                    class="btn btn-success"
                    type="submit"
                    onClick={() => handleCheckOut(User)}
                //   onSubmit={handleCheckOut}

                >CheckOut</Button>
            </div>
        </div>
    )

}
export default ListUser;
