import React, { useState, useEffect } from "react";
import axios from 'axios';
import {  useHistory } from "react-router-dom";
import { Table, Button } from 'react-bootstrap';

import './Styles.css';
// import { useState } from 'react';

function ListUser(props) {

    const [User, setUser] = useState([]);
    console.log(User,'User');

    useEffect(() => {
        getUserList()
    }, [])

    const getUserList = () => {
        axios.get('http://localhost:8001/Admin/getTicket')
            .then((response) => {
                setUser(response.data)
            })
        // .catch((error) => {
        // console.log(error);
        // })
    };

    // const deleteUser = (id) => {
    //     axios.get('http://localhost:8000/User/deleteUser/' + id)
    //         .then((response) => {
    //             getUserList()

    //         })
    // }

    // let history = useHistory();
    // const getUserById = (id) => {

    //     history.push('/Test/'+ id)


    // //     axios.get('http://localhost:8000/User/editUser/' + id)
    // //         .then((response) => {

    // //             console.log(response, 'edit user responce')

    // //         })
    // }


    // const redirect = ()=>{
    //     history.push('/editUser/:id')
    // }

    const emptyToken = () => {
        console.log("hii deleted");
        localStorage.removeItem('savedToken');
        props.history.push('/')
        // localStorage.removeItem('Carts'._id)
        // getItemList()
    }


    return (
        <div>
            <h3>List of User Details</h3>

            <Table hover bordered striped responsive size="sm">

                <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Email</th>
                        {/* <th>Password</th> */}
                        <th>DOB</th>
                        <th>MobileNumber</th>
                        <th>Gender</th>
                        <th>Above_Eighteen</th>
                        <th>Purpose</th>
                        <th>Action</th>
                        {/* <th></th> */}
                    </tr>
                </thead>

                <tbody>
                    {
                        User && User.length > 0 && User.map((user, i) => {

                            return (
                                <tr key={i}>
                                    <td>{i + 1}</td>
                                    <td>{user.Name}</td>
                                    <td>{user.Email}</td>
                                    {/* <td>{user.Password}</td> */}
                                    <td>{user.DOB}</td>
                                    <td>{user.MobileNumber}</td>
                                    <td>{user.Gender}</td>
                                    <td>{user.Above_Eighteen}</td>
                                    <td>{user.Purpose}</td>
                                    <td>{user.Action}</td>


                                    {/* <td>
                                        <Button variant="info" onClick={() => getUserById(user._id)} className="btn btn-primary">Edit</Button>
                                    </td> */}
                                    {/* <td>
                                        <Button onClick={() => deleteUser(user._id)} bsStyle="danger" >Delete</Button>
                                    </td> */}
                                </tr>
                            )
                        })
                    }
                </tbody>
                <div className="col-md-12 text-right">
                <Link to='/'>
                    <Button
                        class="btn btn-success"
                        onClick={emptyToken}
                    // onSubmit={() => handleFirstCheck(Counts)}

                    >LogOut</Button>
                </Link>
            </div>

            </Table>
        </div>
    )

}
export default ListUser;
