import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useCart } from "react-use-cart"

var config = {
    imagePath: "http://localhost:8000/images/",
}

const CouponTable = (props) => {

    const [offers, setOffers] = useState(0);
    const [couponCode, setCouponCode] = useState([]);
    // console.log(couponCode, "couponCode");

    useEffect(() => {
        getItemList();

    }, [])

      useEffect(() => {
        // calculateTotal();
        TotalOfferCalc();
      }, [couponCode])

    const getItemList = () => {
        var myCoupon = JSON.parse(localStorage.getItem('Carts'))
        setCouponCode(myCoupon)
    };

    //   const calculateTotal = () => {
    //     let total = 0;

    //     {
    //       checkOut && checkOut.length > 0 && checkOut.map(i => {

    //         return (
    //           total += parseInt(i.price * i.qty)

    //         )

    //       })
    //     };
    //     setCount(total)
    //   }

      const TotalOfferCalc = () => {
        let totalOffer = 0;

        {
            couponCode && couponCode.length > 0 && couponCode.map(game => {

            if (game.OfferType == 3) {
              totalOffer += parseInt((game.price)- (game.MaxOfferLtd) ) * (game.qty)


            } else if (game.OfferType == 2) {
              totalOffer += parseInt(((game.price) - (game.OfferValue)) * (game.qty))


            } else if (game.OfferType == 1) {
              totalOffer += parseInt((game.price) * (game.qty))

            }
          })
        };
        setOffers(totalOffer)
      }

    return (
        <div>
            <section className="py-4 container">
                <div className="row justify-content-center">
                    <div className="col-12">
                        {/* <h5>Cart({totalUniqueItems}) Total Items:({totalItems})</h5> */}
                        <table className="table table-light table-hover m-0">
                            <thead>
                                <tr>

                                    <th>Actual Price</th>

                                    <th>OfferPrice</th>
                                    {/* <th>Offer type</th> */}
                                </tr>
                            </thead>
                            <tbody>

                                {couponCode && couponCode.length > 0 && couponCode.map(item => {
                                    return (
                                        <tr>

                                            <td>{item.price}</td>

                                            {/* <td>
                                                {(item.qty * item.price)}

                                            </td> */}

                                            {item.OfferType == 3 &&
                        <td>{((item.price)- (item.MaxOfferLtd) ) * (item.qty)}</td>

                      }
                      {
                        item.OfferType == 2 && <td>{((item.price) - (item.OfferValue)) * (item.qty)}</td>
                      }
                      {

                        item.OfferType == 1 &&
                        <td>{(item.price) * (item.qty)}</td>
                      }

                                        </tr>

                                    )

                                })}
                            </tbody>
                            <div className="col-auto ms-auto">
                <h2>Offer Price: ${offers}</h2>
              </div>

                        </table>


                        {/* <div className="col-auto">
                            <button className="btn btn-danger m-2"

                                onclick={() => emptyCart()}>Clear CheckOut1</button>

                        </div> */}
                    </div>
                </div>

            </section>
        </div>
    )

}

export default CouponTable;
