import React, { useState, useEffect } from 'react';
// import ItemCart from "../Components/ItemCart"
// import data from "../Components/Data";
import axios from 'axios';

import { Button, Table } from 'react-bootstrap';
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Badge from "@material-ui/core/Badge";

import { Link } from 'react-router-dom';


// import ButtonGroup from "@material-ui/core/ButtonGroup";
// import button from "@material-ui/core/Button";
// import AddIcon from "@material-ui/icons/Add";
// import RemoveIcon from "@material-ui/icons/Remove";


const Home = (props) => {

  var CartsOld = localStorage.getItem('Carts');
  if(CartsOld == null) {
    CartsOld = [];
  }
  console.log('CartsOld', CartsOld)
  const [data, setData] = useState([])
  const [Carts, setCarts] = useState(CartsOld)
  const [ItemCount] = useState(0);
  const [searchText, setSearchText] = useState(data);
  const [filteredData, setFilteredData]=useState();

  useEffect(() => {
    getItemList()
    searchItem()

  }, [])

  const getItemList = () => {
    axios.get('http://localhost:8000/Admin/getProduct')
      .then((response) => {
        // console.log('reeeeeeeeeeeeeeees',response)
        setData(response.data)
        // console.log('ressssssssssssss', response.data)
        // if (response.data) {
        //   let Product = response.data;
        //   localStorage.setItem('mydata', JSON.stringify(Product));
        // localStorage.setItem("mydata", 'Bearer' + Product)
        // axios.defaults.headers.common['Authorization'] = 'Bearer'+data
        // }
      })


    // .catch((error) => {
    // console.log(error);
    // })
  };

  const searchItem = () => {
    axios.get('http://localhost:8000/Admin/getProduct')
      .then((response) => {
        setData(response.data);
        setFilteredData(response.data);
      })
      .catch(error => {
        console.log('Error getting fake data: ' + error);
      })
  };

   // exclude column list from filter
   const excludeColumns = ["title", "price"];

   const handleSearch = value => {
    setSearchText(value);
    filterData(value);
  };

  // filter records by search text
  const filterData = (value) => {
    console.log('filtreererer', filterData);
    const lowercasedValue = value.toLowerCase().trim();
    if (lowercasedValue === "") setData(data);
    else {
      const filteredData = data.filter(item => {
        return Object.keys(item).some(key =>
          excludeColumns.includes(key) ? false : item[key].toString().toLowerCase().includes(lowercasedValue)
        );
      });
      setData(filteredData);
    }
  }

  const handleSubmit = (data) => {
    // var cartdata = [...Carts, event];
    // setCarts(cartdata);
    // console.log(Carts, "dasdsadasdasdasdasd")
    console.log("come handle click fun");
    props.history.push({
      pathname: "/cart",
      data: {}
    });
    console.log('dataclick', data);
  }

  const quantityChanges = (item, addOrLess) => {
    var dataNew = JSON.parse(JSON.stringify(data));
    const check = dataNew.findIndex(element => element._id === item._id);
    if (check !== -1) {
      if(typeof dataNew[check].qty == 'undefined') {
        dataNew[check].qty = 0;
      }
      if(addOrLess == 'add') {
        dataNew[check].qty++;
      } else {
        dataNew[check].qty--;
      }
      if(dataNew[check].qty < 1) {
        dataNew[check].qty = 1;
      }
      console.log('data[check]', dataNew[check])
      setData(dataNew);
    }
  }

  const addToCart = (item) => {

    var CartsGet = Carts;
    console.log('CartsGet', Carts)
    const check = CartsGet.findIndex(element => element._id === item._id);
    if (check == -1) {
      if(typeof item.qty == 'undefined') {
        item.qty = 1;
      }
      CartsGet.push(item);
    } else {
      // already aded
    }
    setCarts(CartsGet);
    localStorage.setItem('Carts', CartsGet);
    console.log('Carts check', CartsGet, check, item)
  }


  return (


    <div>
      <h1 className="text-center mt-3">All Shopping Products</h1>

      <div className="SearchBar">
        <div style={{ margin: '0 auto', marginTop: '3%' }}>
          <label>Search:</label>
          <input
            placeholder="Type to search..."
            value={searchText}
            type="text"
            onChange={(e) => handleSearch(e.target.value)} />

        </div>

      </div>

      <section className="py-4 container">
        <div className="row justify-content-center">
          {data && data.length > 0 && data.map((item, index) => {
            return (
              <div className="col-11 col-md-6 coll-lg-3 mx-0 mb-4">
                <div class="card p-0 overflow-hidden h-100 shadow" >
                  <img src={item.img} className="card-img-top img-fluid" alt={item.title} />
                  <div class="card-body text-center" >
                    <h5 class="card-title">{item.title}</h5>
                    <h6 class="card-price">$ {item.price}</h6>
                    <p class="card-text">{item.description}</p>
                    {/* <p class="card-qty">{Carts.length}</p> */}
                    {/* <div key={item.id}> */}
                      {/* {`${item.qty}: $${item.price}`} */}
                      {/* <p class="card-qty">{(Carts.length)}</p>  */}
                      {/* <input type="submit" value="+" onClick={() => quantityChanges(Math.max(item.qty * item.price))} /> */}
                    {/* </div> */}
                    <div key={item.id}>
                      =={item.qty}==                      {item.qty?item.qty:'---1'}
                      Add : <input type="submit" value="+" onClick={() => quantityChanges(item, 'add')} />
                      Less : <input type="submit" value="-" onClick={() => quantityChanges(item, 'less')} />
                    </div>
                    <div key={item.id}>
                      <input type="submit" value="Add to Cart" onClick={() => addToCart(item)} />
                    </div>
                  </div>
                </div>
              </div>
            )
          })}
                  <div className="clearboth"></div>
        {data.length === 0 && <span>No records found to display!</span>}


        </div>

      </section>

      <div className="col-md-12 text-center">
        <Badge
          color="secondary"
          // value={items.qantity}
          badgeContent={ItemCount}>
          <ShoppingCartIcon />{" "}
        </Badge>
      </div>

      <div className="col-md-12 text-center">
        <Badge
          color="secondary"
          badgeContent={Carts.length}>
          <ShoppingCartIcon />{" "}
        </Badge>
      </div>
      <div>

        <div className="col-md-12 text-center">
          <Link to='/cart'>
            <Button
              class="btn btn-success"
              // onClick={handleClick}
              onSubmit={() => handleSubmit(Carts)}

            >cart view</Button>
          </Link>
        </div>




      </div>

    </div>

  )
}
export default Home;
