import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { Button, Table } from 'react-bootstrap';
import { useCart } from "react-use-cart"


const CartView = (props) => {

  const [data, setData] = useState([]);

  const [Counts, setCounts] = useState(0);

  // const [cartTotal, setCartTotal] = useState(0);
const { emptyCart } =useCart


  // console.log(Counts, "dasdsadasdasdasdasd")
  // console.log('sfsddfsf', data);


  const handleOnClick = (data) => {
    var cartCount = Counts + data;
    setCounts(cartCount);
    // console.log(Counts, "dasdsadasdasdasdasd", data)
    // var testdata = [...Counts, data];
    // setCounts(testdata);
    // total()

  }





  useEffect(() => {
    getItemList()
    // myData()
    // getCartList()
  }, [])

  const getItemList = () => {
    var myData = JSON.parse(localStorage.getItem('Carts'))
    setData(myData)
  };



  const deleteCarts = (id) => {
    var deletedData = JSON.parse(localStorage)

    // axios.get('http://localhost:8080/User/deleteCarts/' + id)
      .then((response) => {
        getItemList()

      })
  }



  // let myData = JSON.parse(localStorage.getItem('mydata'));
  // // {data.map((item,index)=>{})}
  // // setProduct(myData)
  // const { qty: qty } = myData;
  // console.log('dddddkjhsdkjkdfldddd',typeof data)


  return (
    <div>
      <section className="py-4 container">
        <div className="row justify-content-center">
          <div className="col-12">
            {/* <h5>Cart({totalUniqueItems}) Total Items:({totalItems})</h5> */}
            <table className="table table-light table-hover m-0">


              <tbody>


                { data && data.length > 0 && data.map(item => { 
                return (
                  <tr>
                    <td><img      
                      src={item.img}
                      style={{ height: '6rem' }} /></td>
                    <td>{item.title}</td>
                    <td>{item.price}</td>
                    <td>quantity( {item.qty} )</td>
                    <td>

                      <input type="submit" value="calculate" onClick={() => handleOnClick(item.qty * item.price)} />
                    {/* <span>{Counts.toString()}</span> */}
                    ({item.qty?item.qty:0})

                    </td>

                    <td>
                      <Button onClick={() => deleteCarts(item._id)} bsStyle="danger" >Delete</Button>
                    </td>

                  </tr>

                )

              })}
              </tbody>

              {/* 
                <h5>Cart({totalUniqueItems}) Total Items:({totalItems})</h5> */}

              <div className="col-auto ms-auto">
                <h2>Total Price: ${Counts}</h2>
              </div>

            </table>
          </div>


          <div className="col-auto">
           <button className="btn btn-danger m-2"

           onclick={() => emptyCart()}>Clear Cart</button>  

       </div>
        </div>


      </section>
    </div>
  )

}

export default CartView;
