import React, { useState, useEffect, useHistory } from "react";
import { Button } from 'react-bootstrap';

import { Link } from 'react-router-dom';
import '../Styles.css';
import axios from "axios";
// import validator from 'validator';
import { toast } from 'react-toastify';


function Auth(props) {
  // const history = useHistory();

  const [login, setLogin] = useState('');
  const [EmailError, setEmailError] = useState('');
  const [PasswordError, setPasswordError] = useState('');

const [state, setState] = useState({
  Email:"",
  Password:"",
})
console.log(state,'statestate');
  const InputChange = (event) => {
    switch (event.target.name) {
      case 'Email':
        setState(event.target.value);
        break;
      case 'Password':
        setState(event.target.value);
        break;
      default:
        break;
    }
    validationCheck(event.target);
  }

  const validationCheck = (target) => {
    let isValid = true;

    {
      login && login.length > 0 && login.map(item => {

        if (target.name == 'Email') {
          if (target.value === item.Email) {
            setEmailError('Enter valid Email!')
          } else {
            setEmailError('Enter mail is not match!')
          }


        } else if (target.name == 'Password') {
          if (target.value === item.Password) {
            // setPasswordError('Is a correct Password')
            // } else {
            //   setPasswordError('Is Not valid Password')
            // }
            isValid = false;
            setPasswordError("Please enter your Password.");
          }
        }
      })
    }
  }

  useEffect(() => {
    getLogin()
    getdetails()

  }, [])

  const getdetails =() =>{
    var myData = JSON.parse(localStorage.getItem('savedToken'))

      if(myData){
          props.history.push("/listTicket")
      }
  }

  const getLogin = () => {

    axios.get("http://localhost:8001/User/getUser")
      .then((response) => {
        setLogin(response.data)
      })
  }

  // const emptyToken = () => {
  //   console.log("hii deleted");
  //   localStorage.removeItem('savedToken');
  //   props.history.push('/')
  //   // localStorage.removeItem('Carts'._id)
  //   // getItemList()
  // }


  const handleSubmit = (event) => {
    event.preventDefault();
    const {
      Email, Password
    } = state;
console.log(state.Email,'dddddddddddddddddddddddddd');
    axios.post('http://localhost:8001/User/logUser', {
      Email: Email,
      Password: Password,

    })
      .then((response) => {
        toast.success("Success");
        if (response.data) {

          let token = response.data.data;
          localStorage.setItem("savedToken", JSON.stringify(token))
          axios.defaults.headers.common['Authorization'] = 'Bearer' + token

        }
        //  return response;
        props.history.push("/listTicket")

      })
      .catch((error) => {
        console.log(error)
      })
  }



  return (
    <div className='container'>
      <div className='col-sm-12 center'>
        <h3>Login Page</h3>
      </div>

      <div className='col-sm-12 center'>
        <form onSubmit={handleSubmit}>
          <div className='form-group'>
            {/* <label> Email </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Email"
              name="Email"
              value={state.Email}
              onChange={InputChange}

            />
            <div style={{ fontsize: 4, color: "red" }}>
              {EmailError}</div>
          </div>

          <div className='form-group'>
            {/* <label> Password </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Password"
              name="Password"
              value={state.Password}
              onChange={InputChange}
            />
            <div style={{ fontsize: 4, color: "red" }}>
              {PasswordError}</div>

          </div>
          <div className='form-group'>
            <Button block size="lg"
              className="btn btn-primary"
              type='submit' >Signin</Button> </div>

        </form>
      </div>
      {/* <div className="col-md-12 right">
          <Button
            class="btn btn-success"
            onClick={emptyToken}>LogOut</Button> </div> */}
    </div>
  )

}
export default Auth;
