import React, { useState, useEffect } from 'react';
// import React from "react";
import axios from 'axios';
import './Styles.css';

const SearchProduct = () => {

    const [data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState(data);

  // exclude column list from filter
  const excludeColumns = ["title", "price"];

    useEffect(() => {
        searchItem()
    }, [])


    const searchItem = () => {
        axios.get('http://localhost:8000/Admin/getProduct')
            .then((response) => {
                // console.log('rsssssssssssssssss',response)

                setData(response.data);
                setFilteredData(response.data);
                console.log('ressssssssssssss', response.data)

            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })

    };

    const handleSearch = (e) => {

        let value = e.target.value.toLowerCase();

        console.log('seasdafa', e.target.value);

        let result = [];
        console.log(value);
        result = data.filter((data) => {
        return data.title.search(value) != -1;
        });
        setFilteredData(result);
        }

    return (

        <div className="SearchBar">
            <div style={{ margin: '0 auto', marginTop: '3%' }}>
                <label>Search:</label>
                <input 
                placeholder="Type to search..."
                type="text" 
                onChange={(e) => handleSearch(e.target.value)} />

{/* <input
        style={{ marginLeft: 5 }}
        type="text"
        placeholder="Type to search..."
        value={searchText}
        onChange={e => handleChange(e.target.value)}
      /> */}

            </div>
            <div style={{ padding: 10 }}>
                {filteredData.map((value, index) => {
                    return (

                        <div key={index} className="box" style={{ backgroundColor: value.color }}>
                        <b>title: </b>{value.title}<br />
                        <b>price: </b>{value.price}
                      </div>

                                // <div  key={value.id}>
                                //     {/* {value.title} */}
                                // </div>

                    )
                })}
            </div>

        </div>


    )

}
export default SearchProduct;
