import React, { useState, useEffect, useHistory } from "react";
import { Button } from 'react-bootstrap';

import { Link } from 'react-router-dom';
import '../Styles.css';
import axios from "axios";
// import validator from 'validator';
import { toast } from 'react-toastify';


function Auth(props) {
  // const history = useHistory();

  const [login, setLogin] = useState('');
  const [EmailError, setEmailError] = useState('');
  const [PasswordError, setPasswordError] = useState('');

const [State, setState] = useState({
  Email:"",
  Password:"",
})


  useEffect(() => {
    getLogin()
    getdetails()

  }, [])

  const getdetails =() =>{
    var myData = JSON.parse(localStorage.getItem('savedToken'))

      if(myData){
          props.history.push("/listTicket")
      }
  }

  const getLogin = () => {

    axios.get("http://localhost:8001/User/getUser")
      .then((response) => {
        setLogin(response.data)
      })
  }

  const handleInputChange = (event) => {
    setState((prevProps) => ({
      ...prevProps,
      [event.target.name]: event.target.value

    }));
  };

  // const emptyToken = () => {
  //   console.log("hii deleted");
  //   localStorage.removeItem('savedToken');
  //   props.history.push('/')
  //   // localStorage.removeItem('Carts'._id)
  //   // getItemList()
  // }



  const handleSubmit =(event) =>{
    event.preventDefault();
    const{
        Email,  Password
       } = State;

       if((State.Email == undefined)||(State.Email == null)||(State.Email == "")){
        setEmailError("Please enter your valid email address.")
      }else if((State.Password == undefined)|| (State.Password == null)||(State.Password == "")){
        setPasswordError("Please enter your Password.")
      }


     axios.post('http://localhost:8001/User/logUser',{
     Email: Email,
     Password: Password,
    //  token: token
     })
     .then((response)=>{

       if(response.data){
        let token = response.data.data;
        localStorage.setItem("savedToken", 'Bearer' + token)
        axios.defaults.headers.common['Authorization'] = 'Bearer'+token

       }
       props.history.push("/listTicket")
     })
     .catch((error) => {
       console.log(error)
     })
   }

   const handleClick = () => {
    props.history.push({
        pathname: "/Register",
        data: {}
    });
}


  return (
    <div className='container'>
      <div className='col-sm-12 center'>
        <h3>Login Page</h3>
      </div>

      <div className='col-sm-12 center'>
        <form onSubmit={handleSubmit}>
          <div className='form-group'>
            {/* <label> Email </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Email"
              name="Email"
              value={State.Email}
              onChange={handleInputChange}

            />
            <div style={{ fontsize: 4, color: "red" }}>
              {EmailError}</div>
          </div>

          <div className='form-group'>
            {/* <label> Password </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Password"
              name="Password"
              value={State.Password}
              onChange={handleInputChange}
            />
            <div style={{ fontsize: 4, color: "red" }}>
              {PasswordError}</div>

          </div>
          <div className='form-group'>
            <Button block size="lg"
              className="btn btn-primary"
              type='submit' >Signin</Button> </div>

        </form>
      </div>
      <div className="col-md-12 right">
          <Button
            class="btn btn-success"
            onClick={handleClick}>Register</Button> </div>
    </div>
  )

}
export default Auth;
