import React, { useState, useEffect } from 'react';
// import ItemCart from "../Components/ItemCart"
// import data from "../Components/Data";
import axios from 'axios';

import { Button, Table } from 'react-bootstrap';
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Badge from "@material-ui/core/Badge";

import { Link } from 'react-router-dom';
import SearchProduct from './SearchProduct';

// import ButtonGroup from "@material-ui/core/ButtonGroup";
// import button from "@material-ui/core/Button";
// import AddIcon from "@material-ui/icons/Add";
// import RemoveIcon from "@material-ui/icons/Remove";


const Home = (props) => {


  const [data, setData] = useState([])

  const [Carts, setCarts] = useState([])

  // console.log('aaaaaaaaaa', Carts);


  // const [ItemCount, setItemCount] = useState(0);
  const [ItemCount] = useState(0);



  useEffect(() => {
    getItemList()

  }, [])

  const getItemList = () => {
    axios.get('http://localhost:8000/Admin/getProduct')
      .then((response) => {
        // console.log('reeeeeeeeeeeeeeees',response)

        setData(response.data)
        // console.log('ressssssssssssss', response.data)
        // if (response.data) {
        //   let Product = response.data;
        //   localStorage.setItem('mydata', JSON.stringify(Product));
        // localStorage.setItem("mydata", 'Bearer' + Product)
        // axios.defaults.headers.common['Authorization'] = 'Bearer'+data
        // }
      })


    // .catch((error) => {
    // console.log(error);
    // })
  };





  const handleSubmit = (data) => {

    // var cartdata = [...Carts, event];
    // setCarts(cartdata);
    // console.log(Carts, "dasdsadasdasdasdasd")

    console.log("come handle click fun");
    props.history.push({
      pathname: "/cart",
      data: {}
    });
    console.log('dataclick', data);
  }



  const addTOCount = (item) => {

    let total = 0;

    // console.log(addIt, 'acafsdfs', Carts);
    for (let i = 0; i < Carts.length; i++) {
      total += Carts[i];
    }

    setCarts([...Carts, item])
    console.log(Carts, 'cartttttttt', item);

  }


  const addCartCount = (item, id) => {

    const check = Carts.findIndex(item => item.id === id);
    if (check !== -1) {
      Carts[check].qty++;
      console.log("Quantity updated:", Carts);
    } else {
      Carts.push({ ...Carts.find(item => item.id === id), qty: 1 })
      console.log('The product has been added to cart:', Carts);

    }
    let Product = item;
    localStorage.setItem('mydata', JSON.stringify(Product));
    // setItemCount(item)
    console.log('updatesend', Product)

  }






  return (


    <div>
      <h1 className="text-center mt-3">All Shopping Products</h1>

      <SearchProduct/>

      <section className="py-4 container">
        <div className="row justify-content-center">
          {data && data.length > 0 && data.map((item, index) => {


            return (
              <div className="col-11 col-md-6 coll-lg-3 mx-0 mb-4">
                <div class="card p-0 overflow-hidden h-100 shadow" >



                  <img src={item.img} className="card-img-top img-fluid" alt={item.title} />


                  <div class="card-body text-center" >
                    <h5 class="card-title">{item.title}</h5>
                    <h6 class="card-price">$ {item.price}</h6>
                    <p class="card-text">{item.description}</p>
                    {/* <p class="card-qty">{Carts.length}</p> */}


                    <div key={item.id}>
                      {/* {`${item.qty}: $${item.price}`} */}
                      {/* <p class="card-qty">{(Carts.length)}</p>  */}
                      <input type="submit" value="+" onClick={() => addTOCount(Math.max(item.qty * item.price))} />
                    </div>

                    <div key={item.id}>
                      {/* {`${item.qty}: $${item.price}`} */}
                      {`${item.qty}`}
                      <input type="submit" value="+" onClick={() => addTOCount(item)} />
                    </div>


                    <div key={item.id}>
                      {/* {`${item.qty}: $${item.price}`} */}
                      {/* {`${item.qty}`} */}
                      <input type="submit" value="Send" onClick={() => addCartCount(item)} />
                    </div>


                  </div>


                </div>
              </div>

            )
          })}

        </div>

      </section>

      <div className="col-md-12 text-center">
        <Badge
          color="secondary"
          // value={items.qantity}
          badgeContent={ItemCount}>
          <ShoppingCartIcon />{" "}
        </Badge>
      </div>

      <div className="col-md-12 text-center">
        <Badge
          color="secondary"
          badgeContent={Carts.length}>
          <ShoppingCartIcon />{" "}
        </Badge>
      </div>
      <div>

        <div className="col-md-12 text-center">
          <Link to='/cart'>
            <Button
              class="btn btn-success"
              // onClick={handleClick}
              onSubmit={() => handleSubmit(Carts)}

            >cart view</Button>
          </Link>
        </div>




      </div>

    </div>

  )
}
export default Home;
