import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useCart } from "react-use-cart";

import { Button, Table } from 'react-bootstrap';

import { Link } from 'react-router-dom';

var config = {
  imagePath: "http://localhost:8000/images/",
}

const CartView = (props) => {
  const [data, setData] = useState([]);
  const [Counts, setCounts] = useState(0);
  const [OfferCounts, setOfferCounts] = useState(0);

  // const { emptyCart } = useCart
  // const {offer} =  Counts -(data.MaxOfferLtd* data.qty);

  // console.log('offeroffer', offer);


  useEffect(() => {
    getItemList();

  }, [])

  useEffect(() => {
    calculateTotal();
    calculateOfferTotal();
  }, [data])

  const getItemList = () => {

    var myData = JSON.parse(localStorage.getItem('Carts'))
    setData(myData)
  };

  const calculateTotal = () => {
    let total = 0;

    {
      data && data.length > 0 && data.map(item => {


        return (

          total += parseInt((item.price * item.qty))

        )

      })
    };
    setCounts(total)
  }

  const calculateOfferTotal = () => {
    let total = 0;

    {
      data && data.length > 0 && data.map(item => {


        return (

          total += parseInt((item.price * item.qty) - (item.MaxOfferLtd * item.qty))

        )

      })
    };
    setOfferCounts(total)
  }


  //   const deleteCarts = (i) => {
  // // console.log(i, "id");
  //     i.splice(i, 1);
  //       localStorage.setItem('Carts', JSON.stringify([]));
  //     getItemList()    

  //   }


  const deleteCarts = () => {

    var Carts = JSON.parse(localStorage.getItem("Carts")); //update
    for (var i = 0; i < Carts.length; i++) {
      // var Carts = JSON.parse(Carts[i]);
      if (Carts._id == 3) {
        Carts.splice(i, 1);
      }
    }
    Carts = JSON.stringify(Carts); //Restoring object left into items again
    console.log(Carts, 'itemsitemsitems-----------------');
    localStorage.setItem("Carts", Carts);

  }

  const handleFirstCheck = (data) => {
    console.log("come handle click fun");
    props.history.push({
      pathname: "/CheckOut1",
      data: {}
    });
  }

  const emptyCart = () => {

    console.log("hii deleted");
    // localStorage.removeItem('Carts'._id)
    getItemList()
    // localStorage.removeItem('Carts');
  }


  return (
    <div>
      <section className="py-4 container">
        <div className="row justify-content-center">
          <div className="col-12">
            {/* <h5>Cart({totalUniqueItems}) Total Items:({totalItems})</h5> */}
            <table className="table table-light table-hover m-0">
              <thead>
                <tr>
                  <th>image</th>
                  <th>title</th>
                  <th>price</th>
                  <th>quantity</th>
                  <th>Sub_total</th>
                </tr>
              </thead>
              <tbody>

                {data && data.length > 0 && data.map((item, i) => {
                  return (
                    <tr key={i}>
                      <td><img
                        src={config.imagePath + 'product/' + item.image}
                        style={{ height: '6rem' }} /></td>
                      <td>{item.title}</td>
                      <td>{item.price}</td>
                      <td>( {item.qty} )</td>

                      <td>
                        {(item.MaxOfferLtd) &&
                          ((item.qty * item.price) - (item.MaxOfferLtd * item.qty))
                        }
                        <td>
                          {(item.qty * item.price)}
                        </td>


                      </td>


                      {/* <td>
                        <input type="submit" value="calculate" onClick={() => handleOnClick(item.qty * item.price)} />

                        </td> */}


                      {/* <td>
                        <Button onClick={() => deleteCarts(item._id)} bsStyle="danger" >Delete</Button>

                      </td> */}


                    </tr>

                  )

                })}
              </tbody>

              {/* 
                <h5>Cart({totalUniqueItems}) Total Items:({totalItems})</h5> */}

              <div className="col-auto ms-auto">
                <h2>Total Price: ${Counts}</h2>
              </div>

            </table>
          </div>


          <div className="col-auto">
                <button className="btn btn-danger m-2"
                  bsStyle="danger"
                  onclick={emptyCart}>Clear Cart</button>

          </div>
        </div>

        <div className="col-md-12 text-right">
          <Link to='/CheckOut1'>
            <Button
              class="btn btn-success"
              // onClick={handleClick}
              onSubmit={() => handleFirstCheck(Counts)}

            >Check Out 1</Button>
          </Link>
        </div>

        {/* <div className="col-auto">
            <button className="btn btn-primary"

              onclick={() => handleFirstCheck(Counts)}>Check Out 1</button>

          </div> */}

      </section>
    </div>
  )

}

export default CartView;
