import React, { useState, useEffect, useHistory } from "react";
import { Button } from 'react-bootstrap';

import { Link } from 'react-router-dom';
import '../Styles.css';
import axios from "axios";
// import validator from 'validator';
import { toast } from 'react-toastify';

function Register(props) {
  // const [login, setLogin] = useState('');
  const [EmailError, setEmailError] = useState('');
  const [PasswordError, setPasswordError] = useState('');
  const [State, setState] = useState({
    Email: "",
    Password: "",

  });

  const InputChange = (event) => {
    switch (event.target.name) {
      case 'Email':
        setState(event.target.value);
        break;
      case 'Password':
        setState(event.target.value);
        break;
      default:
        break;
    }
    validationCheck(event.target);
  }

  const validationCheck = (target) => {
    let isValid = true;

        if (target.name == 'Email') {
          let regExp = RegExp(/^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/)
          let emailpattern = regExp;
          let passstatusval = emailpattern.test(target.value);
          if (!passstatusval) {
            setEmailError("enter your valid Email")
          } else {
            setEmailError("")
          }
        }
        // else if(target.name == 'Password'){
        //   // var pass = event.target.value;
        //   let reg = '/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,32}$/';
        //   let passpattern = reg;
        //   let pass = passpattern.test(target.value);
        //   if (!pass) {
        //     setPasswordError("Password should be more than 8 characters");
        //   } else{
        //     setPasswordError("");
        //   }
        // }
        //  

  }

  const valid = (event) => {

    var nameoferr; 
    if (EmailError !="") {
      alert()
      nameoferr = "Email Required";   
      console.log(nameoferr,'nameoferrnameoferr');
      setEmailError(nameoferr)
      return 0;
    }
    if (PasswordError !="") {
      nameoferr= "Password Required";   

      setPasswordError(nameoferr)
      return 0;
    }
    return 1
  }


  const handleSubmit = (event) => {
    event.preventDefault();
    var validstatus = valid();
    const{
      Email,  Password
     } = State;


     if (validstatus === 1) {
      console.log("Valid")
      // alert("hiiiii")
      // console.log("Valid")

      axios.post("http://localhost:8001/User/addUser", {
        Email: Email,
        Password: Password,
      })
        .then(response => {
          toast.success("Success");
          // console.log(res);
          // props.history.push('/Register/listTicket')
        })
        .catch(err => {
          console.log(err.response);
          toast.error(err.response.data)
        })
      }
     else {
      toast.warning("Please Fill values");
      console.log(("notvalid"));
      return 0;
    }
  }


  return (
    <div className='container'>
      <div className='col-sm-12 center'>
        <h3>Login Page</h3>
      </div>

      <div className='col-sm-12 center'>
        <form onSubmit={handleSubmit}>
          <div className='form-group'>
            {/* <label> Email </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Email"
              name="Email"
              value={State.Email}
              onChange={InputChange}

            />
            <div style={{ fontsize: 4, color: "red" }}>
              {EmailError}</div>
          </div>

          <div className='form-group'>
            {/* <label> Password </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Password"
              name="Password"
              value={State.Password}
              onChange={InputChange}
            />
            <div style={{ fontsize: 4, color: "red" }}>
              {PasswordError}</div>

          </div>
          <div className='form-group'>
            <Button block size="lg"
              className="btn btn-success btn-lg float-left"
              type='submit' >Submit</Button> </div>

        </form>
      </div>
      {/* <div className="col-md-12 right">
          <Button
            class="btn btn-success"
            onClick={emptyToken}>LogOut</Button> </div> */}
    </div>
  )

}
export default Register;
