import React, { useState, useEffect, useHistory } from "react";
import { Button } from 'react-bootstrap';

import { Link } from 'react-router-dom';
import '../Styles.css';
import axios from "axios";
// import validator from 'validator';
// import { toast } from 'react-toastify';

function Register(props) {
  const [login, setLogin] = useState('');
  const [EmailError, setEmailError] = useState('');
  const [PasswordError, setPasswordError] = useState('');
  const [State, setState] = useState({
    Email: "",
    Password: "",

  });

  const InputChange = (event) => {
    switch (event.target.name) {
      case 'Email':
        setState(event.target.value);
        break;
      case 'Password':
        setState(event.target.value);
        break;
      default:
        break;
    }
    validationCheck(event.target);
  }

  const validationCheck = (target) => {
    let isValid = true;

    {
      login && login.length > 0 && login.map(item => {

        if (target.name == 'Email') {
          if (target.value === item.Email) {

            setEmailError('Enter valid Email!')
          } else if (target.value !== item.Email) {
            setEmailError('Enter mail is not match!')

          }
        } else if (target.name == 'Password') {
          if (target.value === item.Password) {
            isValid = false;
            setPasswordError("Please enter your Password.");
          }
        }
      })
    }
  }

  const handleSubmit = () => {

  }

  return (
    <div className='container'>
      <div className='col-sm-12 center'>
        <h3>Login Page</h3>
      </div>

      <div className='col-sm-12 center'>
        <form onSubmit={handleSubmit}>
          <div className='form-group'>
            {/* <label> Email </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Email"
              name="Email"
              value={State.Email}
              onChange={InputChange}

            />
            <div style={{ fontsize: 4, color: "red" }}>
              {EmailError}</div>
          </div>

          <div className='form-group'>
            {/* <label> Password </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Password"
              name="Password"
              value={State.Password}
              onChange={InputChange}
            />
            <div style={{ fontsize: 4, color: "red" }}>
              {PasswordError}</div>

          </div>
          <div className='form-group'>
            <Button block size="lg"
              className="btn btn-primary"
              type='submit' >Submit</Button> </div>

          <div class="form-group">
            <button class="btn btn-success btn-lg float-left"
              type="submit">
              Submit
            </button>
          </div>

        </form>
      </div>
      {/* <div className="col-md-12 right">
          <Button
            class="btn btn-success"
            onClick={emptyToken}>LogOut</Button> </div> */}
    </div>
  )

}
export default Register;
