import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from "react-router-dom";
import { Table, Button } from 'react-bootstrap';
import moment from 'moment';

const CheckOut = (props) => {
  const [checkOut, setcheckOut] = useState([]);
  const [Data, setData] = useState([]);
  // console.log(Data,"DataDataData");


  useEffect(() => {
    getItemList();
    getData();
  }, [])

  const getItemList = () => {
    var mycheckOut = JSON.parse(localStorage.getItem('Carts'))
    setcheckOut(mycheckOut)
  };


  // history.push('/cart/' + id)
  // axios.get('http://localhost:8001/User/buyTicket' + id)
  //     .then((response) => {
  //         console.log(response, 'Buy responce')
  //     })


  const getData = (id) => {
    axios({
      method: "get",
      url: 'http://localhost:8001/Admin/getTicket',
      data: {},

      // headers: { "Content-Type": "multipart/form-data" },
    }).then((response) => {
      setData(response.data)
    })
      .catch((error) => {
        console.log(error)
      })
  }

  useEffect(() => {
    checkValue();
  }, [Data])

  const checkValue = (item) => {

    var dataCopy = JSON.parse(JSON.stringify(checkOut));
    console.log("checkelementdataCopy", dataCopy);

    Data && Data.length > 0 && Data.map(item => {
      dataCopy.push({ Ticket_Id: item.Ticket_Id, Ticket_Name: item.Ticket_Name, Price: item.Price })

    })
    setcheckOut(dataCopy)

  }

  return (
    <div>
      <section className="py-4 container">
        <div className="row justify-content-center">
          <div className="col-12">
            {/* <h5>Cart({totalUniqueItems}) Total Items:({totalItems})</h5> */}
            <table className="table table-light table-hover m-0">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Ticket_Id</th>
                  <th>Ticket_Name</th>
                  <th>Price</th>
                  <th>qty</th>
                  {/* <th>Start_Date</th>
                  <th>End_Date</th> */}
                  {/* <th>Status</th> */}
                  {/* <th>Action_qty</th> */}
                </tr>
              </thead>
              <tbody>
                {
                  checkOut && checkOut.length > 0 && checkOut.map((item, i) => {
                    return (
                      <tr key={i}>
                        <td>{i + 1}</td>
                        <td>{item.Ticket_Id}</td>
                        <td>{item.Ticket_Name}</td>
                        <td>{item.Price}</td>
                        <td>{item.Quantity}</td>
                        {/* <td>{moment.utc(item.Start_Date).format('MMM DD YYYY ')}</td>               
                        <td>{moment.utc(item.End_Date).format('MMM DD YYYY')}</td> */}
                        {/* <td>{item.Status}</td> */}


                      </tr>
                    )
                  })
                }
              </tbody>

            </table>
          </div>
        </div>
      </section>
    </div>
  )

}

export default CheckOut;
