import React, { useState, useEffect, useHistory } from "react";
import { Button } from 'react-bootstrap';

import { Link } from 'react-router-dom';
import '../Styles.css';
import axios from "axios";
// import validator from 'validator';
import { toast } from 'react-toastify';


function Auth(props) {
  // const history = useHistory();
  const [login, setLogin] = useState('');
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [EmailError, setEmailError] = useState('');
  const [PasswordError, setPasswordError] = useState('');

  const userdetail = {
    Email: email,
    Password: password,
  }

  const InputChange = (event) => {
    switch (event.target.name) {
      case 'Email':
        setEmail(event.target.value);
        setEmailError("");
        break;
      case 'Password':
        setPassword(event.target.value);
        setPasswordError("")
        break;
      default:
        break;
    }
    validationCheck(event.target);
  }

  const validationCheck = (target) => {
        if (target.name == 'Email') {
          if(userdetail.Email==''){
            toast.warning("Enter Email Id");
            return 0;
          } 
        } else if (target.name == 'Password') {
          if(userdetail.Password==''){
            toast.warning("Enter Password");
            return 0;
          }
    }
  }

  useEffect(() => {
    getLogin()
    getdetails()

  }, [])

  const getdetails =() =>{
    var myData = JSON.parse(localStorage.getItem('savedToken'))

      if(myData){
          props.history.push("/listTicket")
      }
  }

  const getLogin = () => {
    axios.get("http://localhost:8001/User/getUser")
      .then((response) => {
        setLogin(response.data)
      })
  }

  // const emptyToken = () => {
  //   console.log("hii deleted");
  //   localStorage.removeItem('savedToken');
  //   props.history.push('/')
  //   // localStorage.removeItem('Carts'._id)
  //   // getItemList()
  // }


  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('http://localhost:8001/User/logUser', userdetail)
      .then((response) => {
        toast.success("Success");
        if (response.data) {
          let token = response.data.data;
          localStorage.setItem("savedToken", JSON.stringify(token))
          axios.defaults.headers.common['Authorization'] = 'Bearer' + token
        }
        //  return response;
        props.history.push("/listTicket")

      })
      .catch((error) => {
        console.log(error)
      })
  }

  return (
    <div className='container'>
      <div className='col-sm-12 center'>
        <h3>Login Page</h3>
      </div>

      <div className='col-sm-12 center'>
        <form onSubmit={handleSubmit}>
          <div className='form-group'>
            {/* <label> Email </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Email"
              name="Email"
              value={email}
              onChange={InputChange}

            />
            <div style={{ fontsize: 4, color: "red" }}>
              {EmailError}</div>
          </div>

          <div className='form-group'>
            {/* <label> Password </label> */}
            <input
              className="form-control"
              type="text"
              placeholder="Password"
              name="Password"
              value={password}
              onChange={InputChange}
            />
            <div style={{ fontsize: 4, color: "red" }}>
              {PasswordError}</div>

          </div>
          <div className='form-group'>
            <Button block size="lg"
              className="btn btn-primary"
              type='submit' >Signin</Button> </div>

        </form>
      </div>
      {/* <div className="col-md-12 right">
          <Button
            class="btn btn-success"
            onClick={emptyToken}>LogOut</Button> </div> */}
    </div>
  )

}
export default Auth;
