import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useHistory } from "react-router-dom";
import { Table, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Styles.css';
import moment from 'moment';
// import { useState } from 'react';

function ListUser(props) {


    const [Carts, setCarts] = useState(CartsOld);
    console.log(Carts,"CartsCartsCarts");



    const [User, setUser] = useState([]);
    const [searchText, setSearchText] = useState(User);

    useEffect(() => {
        getUserList()
    }, [])

    const getUserList = () => {
        axios.get('http://localhost:8001/Admin/getTicket')
            .then((response) => {
                setUser(response.data)
            })
        // .catch((error) => {
        // console.log(error);
        // })
    };

    useEffect(() => {



        });

    // let history = useHistory();
    const getUserById = (item) => {
        console.log(item,"itemitemitem");

        let hastoken=JSON.parse(localStorage.getItem('savedToken'));
        if(hastoken){
            var CartsCopy = JSON.parse(JSON.stringify(Carts));
            const check = CartsCopy.findIndex(element => element._id === item._id);
            if (check == -1) {
                if (typeof item.qty == 'undefined') {
                  item.qty = 1;
                }
                CartsCopy.push(item);
              } else {
                // already add
                alert("Already selected this cart")
              }
              setCarts(CartsCopy);
        }
       else if(!hastoken){
            alert("go first login")
            props.history.push("/login")
        }
        // history.push('/cart/' + id)
        // axios.get('http://localhost:8001/User/buyTicket' + id)
        //     .then((response) => {

        //         console.log(response, 'Buy responce')

        //     })
    }

    //   // exclude column list from filter
    //   const excludeColumns = ["Price"];

    //     const handleSearch = value => {
    //         setSearchText(value);
    //         filterData(value);
    //       };

    // //   // filter records by search text
    // //   const filterData = (value) => {
    // //     // console.log('filtreererer', filterData);
    // //     const lowercasedValue = value.toLowerCase().trim();
    // //     if (lowercasedValue === "") setUser(User);
    // //     else {
    // //       const filteredUser = User.filter(item => {   
    // //         return Object.keys(item).some(key =>          
    // //           excludeColumns.includes(key) ? false : item[key].toString().toLowerCase().includes(lowercasedValue)
    // //         );
    // //       });
    // //       setUser(filteredUser);

    // //     }

    // //   }


    // exclude column list from filter
    const handleSearch = value => {
        setSearchText(value);
        filterData(value);
    };

    // filter records by search text
    const filterData = (value) => {
        let lowercasedValue = value.trim();   
        let Search;

        {
            User && User.length > 0 && User.map(item => {
                 Search= item.Price

             })
        }    
        let filteredUser = User.filter(el => el.Price == Search)
        // console.log(filteredUser,"filteredUserlowercasedValue",lowercasedValue);

    }


    const emptyToken = () => {
        console.log("hii deleted");
        localStorage.removeItem('savedToken');
        // props.history.push('/login')
        props.history.push('/login')

        // localStorage.removeItem('Carts'._id)
        // getItemList()
    }


    return (
        <div>
            <h3>List of Lottery_Ticket Details</h3>

            <section>
                <div className="SearchBar">
                    <div style={{ margin: '0 auto', marginTop: '3%' }}>
                        <label>Search:</label>
                        <input
                            placeholder="Type to search..."
                            value={searchText}
                            type="text"
                            onChange={(e) => handleSearch(e.target.value)}
                        />

                    </div>
                </div>
            </section>

            <Table hover bordered striped responsive size="sm">

                <thead>
                    <tr>
                        <th>#</th>
                        <th>Ticket_Id</th>
                        <th>Ticket_Name</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Start_Date</th>
                        <th>End_Date</th>
                        <th>Status</th>
                        <th>Buy_Ticket</th>
                        {/* <th></th> */}
                    </tr>
                </thead>

                <tbody>
                    {
                        User && User.length > 0 && User.map((item, i) => {

                            return (
                                <tr key={i}>
                                    <td>{i + 1}</td>
                                    <td>{item.Ticket_Id}</td>
                                    <td>{item.Ticket_Name}</td>
                                    <td>{item.Price}</td>
                                    <td>{item.Quantity}</td>
                                    {/* <td>{user.Start_Date  ",h:mm:ss"}</td> */}
                                    <td>{moment.utc(item.Start_Date).format('MMM DD YYYY ')}</td>
                                    {/* <td>{user.End_Date}</td> */}
                                    <td>{moment.utc(item.End_Date).format('MMM DD YYYY')}</td>
                                    <td>{item.Status}</td>

                                    <td>
                                        <Button variant="info" onClick={() => getUserById(item._id)} className="btn btn-primary">Buy</Button>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
                <div className="col-md-12 text-center">
                    <Link to='/login'>
                        <Button
                            class="btn btn-success"
                            onClick={emptyToken}
                        >LogOut</Button>
                    </Link>
                </div>

            </Table>
        </div>
    )

}
export default ListUser;
